const JSON_OUTCOMES_FIELD="option-result pair";class FortuneEngine{constructor(e){this.app_name=e,this.outcomes=null}async db_reader(e){console.log(e),await fetch(e).then((e=>e.json())).then((e=>{this.outcomes=e[JSON_OUTCOMES_FIELD],this.db_dump()})).catch((e=>console.error(e)))}set_outcomes(e){this.outcomes=e}db_dump(){console.log(this.outcomes)}get_random_subset(e){const o=[...this.outcomes];for(let e=0;e<o.length;e++){const t=Math.floor(Math.random()*this.outcomes.length),n=o[e];o[e]=o[t],o[t]=n}return o.slice(0,e)}}const app_name="cartomancy",db_name="cartomancy.json",engine=new FortuneEngine(app_name);engine.db_reader(db_name).then((()=>{for(let e=0;e<=8;e++)console.log(`${e}-permutation:`,engine.get_random_subset(e))}));export default FortuneEngine;
